import { css } from "styled-components";
import { POSITIONS, POPOVER_SPACE_BETWEEN } from "../consts";

const resolvePopoverPosition = ({
  position,
  containerTop,
  containerHeight,
  popoverHeight,
  overlapped
}) => {
  if (position === POSITIONS.TOP) {
    return css(["top:", "px;"], Math.floor(containerTop - popoverHeight - (overlapped ? -containerHeight : POPOVER_SPACE_BETWEEN)));
  }

  if (position === POSITIONS.BOTTOM) {
    return css(["top:", "px;"], Math.floor(containerTop + (overlapped ? 0 : containerHeight + POPOVER_SPACE_BETWEEN)));
  }

  return null;
};

export default resolvePopoverPosition;